import { createStore } from 'vuex';
// import VuexPersistence from 'vuex-persist';
// const vuexLocal = new VuexPersistence({
// 	key: 'flywheel_store',
// 	storage: window.localStorage
// })
 const store = createStore({
	state: {
		usdot_number: Number,
		usdot_data: {},
		page_valid: false,
		owner_is_contact: false,
		insurance_effective_date: "",
		brokerage_authority: String,
		plan_selected: 0,
		vin_data: Array,
		show_payment: false,
		prev_route: Object,
		curr_route: Object,
		next_route: Object,
		prev_route_index: Number,
		curr_route_index: Number,
		next_route_index: Number,
		plan_options: [
			{
				selected: true,
				deductible: {
					label: "No Deductible",
					amount: 0
				},
				premium: {
					label: '$12,000',
					amount: 12000
				}
			},
			{
				selected: false,
				deductible: {
					label: "$1,000",
					amount: 1000
				},
				premium: {
					label: '$10,000',
					amount: 10000
				}
			},
			{
				selected: false,
				deductible: {
					label: "$5,000",
					amount: 5000
				},

				premium: {
					label: '$9,000',
					amount: 9000
				}
			}
		],		
		cargo: [
			{
				carried: false,
				name: 'General Freight',
				percentage: 0
			},
			{
				carried: false,
				name: 'Bulk Liquid',
				percentage: 0
			},
			{
				carried: false,
				name: 'Furniture',
				percentage: 0
			},
			{
				carried: false,
				name: 'Beer',
				percentage: 0
			},
			{
				carried: false,
				name: 'Live Animals',
				percentage: 0
			},
			{
				carried: false,
				name: 'Refrigerated Goods',
				percentage: 0
			}
		]
	},
	mutations: {
		updateRouteIndex(state, index) {
			state.prev_route_index = index - 1;
			state.curr_route_index = index;
			state.next_route_index = index + 1;
		},
		updatePrevRoute(state, route) {
			state.prev_route = route;
		},
		updateCurrentRoute(state, route) {
			state.curr_route = route;
		},
		updateNextRoute(state, route) {
			state.next_route = route;
		},
		updateShowPayment(state, show_payment){
			state.show_payment = show_payment;
		},
		updateDOTNumber(state, usdot_number) {
			state.usdot_number = usdot_number;
		},

		updateDOTData(state, usdot_data) {
			function abbrState(input, to) {
				var states = [
					['Arizona', 'AZ'],
					['Alabama', 'AL'],
					['Alaska', 'AK'],
					['Arkansas', 'AR'],
					['California', 'CA'],
					['Colorado', 'CO'],
					['Connecticut', 'CT'],
					['Delaware', 'DE'],
					['Florida', 'FL'],
					['Georgia', 'GA'],
					['Hawaii', 'HI'],
					['Idaho', 'ID'],
					['Illinois', 'IL'],
					['Indiana', 'IN'],
					['Iowa', 'IA'],
					['Kansas', 'KS'],
					['Kentucky', 'KY'],
					['Louisiana', 'LA'],
					['Maine', 'ME'],
					['Maryland', 'MD'],
					['Massachusetts', 'MA'],
					['Michigan', 'MI'],
					['Minnesota', 'MN'],
					['Mississippi', 'MS'],
					['Missouri', 'MO'],
					['Montana', 'MT'],
					['Nebraska', 'NE'],
					['Nevada', 'NV'],
					['New Hampshire', 'NH'],
					['New Jersey', 'NJ'],
					['New Mexico', 'NM'],
					['New York', 'NY'],
					['North Carolina', 'NC'],
					['North Dakota', 'ND'],
					['Ohio', 'OH'],
					['Oklahoma', 'OK'],
					['Oregon', 'OR'],
					['Pennsylvania', 'PA'],
					['Rhode Island', 'RI'],
					['South Carolina', 'SC'],
					['South Dakota', 'SD'],
					['Tennessee', 'TN'],
					['Texas', 'TX'],
					['Utah', 'UT'],
					['Vermont', 'VT'],
					['Virginia', 'VA'],
					['Washington', 'WA'],
					['West Virginia', 'WV'],
					['Wisconsin', 'WI'],
					['Wyoming', 'WY'],
				];
				/* eslint-disable no-undef */
				if (to == 'abbr'){
					input = input.replace(/\w\S*/g, function(txt){return txt.charAt(0).toUpperCase() + txt.substr(1).toLowerCase();});
					for(let i = 0; i < states.length; i++){
						if(states[i][0] == input){
							return(states[i][1]);
						}
					}    
				} else if (to == 'name'){
					input = input.toUpperCase();
					for(let i = 0; i < states.length; i++){
						if(states[i][1] == input){
							return(states[i][0]);
						}
					}    
				}
			}
			console.log(abbrState(state.usdot_data.phyState))
			state.usdot_data.phyState = abbrState(state.usdot_data.phyState)
			state.usdot_data = usdot_data;
		},

		addVin(state, vin_data) {
			state.vin_data.push(vin_data);
		},

		updateOwnerIsContact(state, owner_is_contact) {
			state.owner_is_contact = owner_is_contact;
		},

		updateBrokerageAuthority(state, brokerage_authority) {
			state.brokerage_authority = brokerage_authority;
		},

		updateInsuranceEffectiveDate(state, insurance_effective_date) {
			state.insurance_effective_date = insurance_effective_date;
		},

		updateCargo(state, cargo) {
			state.cargo = cargo;
		},

		updatePlans(state, plan_selected) {
			state.plan_options.forEach(function(opt){
				if(opt.deductible.amount == plan_selected) {
					opt.selected = true;
				} else {
					opt.selected = false;
				}
			});			
			state.plan_selected = plan_selected;
		},

		updatePageValid(state, page_valid) {
			state.page_valid = page_valid;
		}
	},
	actions: {
	},
	// plugins: [vuexLocal.plugin]
});

export default store;
